// Classificador inteligente de mensagens
const messageData = $('Extract Message Data').first().json.messageData;
const messageText = (messageData.messageText || messageData.imageCaption || '').toLowerCase().trim();

// Patterns para classificação
const patterns = {
  EXPRESS: [
    // Cumprimentos básicos
    /^(oi|olá|hey|e aí|salve|bom dia|boa tarde|boa noite)/i,
    /^(como (está|vai|anda)|tudo bem|beleza|tranquilo)/i,

    // Confirmações simples
    /^(ok|okay|entendi|beleza|show|perfeito|legal|valeu)/i,
    /^(obrigad|brigad|vlw|thanks)/i,

    // Navegação/Comandos
    /^(menu|ajuda|help|comandos|início|start)/i,
    /^(cancelar|sair|parar|tchau|até)/i,

    // Status muito simples
    /^(como estou|status)/i
  ],

  STANDARD: [
    // Água simples
    /^tomei \d+ml/i,
    /^bebi \d+ml/i,
    /^\d+ml de água/i,
    /(água|hidrat)/i,

    // Consultas de resumo
    /^(resumo|como estamos|minhas metas)/i,
    /^(total de hoje|quanto (consumi|bebi))/i,

    // Perguntas educativas simples
    /(posso comer|qual melhor|é bom|faz mal)/i,
    /(dica|sugestão|recomend)/i,
    /(quantas calorias|macro)/i
  ],

  NUTRITION: [
    // Registro de refeições
    /(comi|almocei|jantei|lanchei|tomei café)/i,
    /(café da manhã|almoço|jantar|lanche)/i,

    // Quantidades específicas
    /\d+g|\d+ gramas/i,
    /\d+ unidade|\d+ fatia|\d+ colher/i,
    /\d+ml.*[^água]/i, // ml que não seja de água

    // Alimentos específicos com detalhes
    /(proteína|carboidrato|caloria|fibra).*\d/i,
    /(arroz|feijão|carne|frango|peixe|ovo|pão|leite).*\d/i,

    // Análises complexas
    /(relatório|análise completa|feedback|balanço)/i,
    /(déficit|superávit|meta diária)/i
  ]
};

// Função para classificar
function classifyMessage(text, messageData) {
  // Casos especiais prioritários
  if (messageData.hasImage) {
    return 'VISION';
  }

  if (messageData.hasAudio) {
    return 'NUTRITION'; // Áudio geralmente é para registrar refeições
  }

  // Se mensagem muito curta (< 3 palavras), provavelmente é EXPRESS
  const wordCount = text.split(/\s+/).length;
  if (wordCount === 1 && text.length < 10 && !/^(resumo|água|metas|status)$/i.test(text)) {
    return 'EXPRESS';
  }

  // Verificar patterns por ordem de prioridade
  for (const [category, patternList] of Object.entries(patterns)) {
    for (const pattern of patternList) {
      if (pattern.test(text)) {
        return category;
      }
    }
  }

  // Heurísticas adicionais

  // Se contém números + unidades de medida = NUTRITION
  if (/\d+.*?(g|ml|mg|kg|unidade|fatia|colher|xícara|copo)/i.test(text)) {
    return 'NUTRITION';
  }

  // Se lista vários alimentos = NUTRITION
  const foodWords = text.match(/(arroz|feijão|carne|frango|peixe|ovo|pão|leite|queijo|presunto|salada|tomate|alface|batata|macarrão|massa|suco|café|chá)/gi);
  if (foodWords && foodWords.length >= 2) {
    return 'NUTRITION';
  }

  // Se mensagem longa (> 50 chars) e não foi classificada = NUTRITION
  if (text.length > 50) {
    return 'NUTRITION';
  }

  // Default para mensagens médias
  return 'STANDARD';
}

// Classificar a mensagem
const classification = classifyMessage(messageText, messageData);

// Configurações por tipo
const config = {
  EXPRESS: {
    queue: 'queue:express',
    model: 'gpt-4o-mini',
    priority: 1,
    timeout: 3000,
    maxRetries: 2,
    estimatedTokens: 150
  },
  STANDARD: {
    queue: 'queue:standard', 
    model: 'gpt-4o-mini',
    priority: 2,
    timeout: 8000,
    maxRetries: 3,
    estimatedTokens: 500
  },
  NUTRITION: {
    queue: 'queue:nutrition',
    model: 'gpt-4o',
    priority: 3,
    timeout: 30000,
    maxRetries: 5,
    estimatedTokens: 2000
  },
  VISION: {
    queue: 'queue:vision',
    model: 'gpt-4o',
    priority: 4,
    timeout: 45000,
    maxRetries: 3,
    estimatedTokens: 3000
  }
};

// Detectar situações especiais
const specialFlags = {
  isGreeting: /^(oi|olá|bom dia|boa tarde|boa noite)/i.test(messageText),
  isThankYou: /^(obrigad|brigad|valeu|thanks)/i.test(messageText),
  isQuestion: messageText.includes('?'),
  hasNumbers: /\d/.test(messageText),
  isList: messageText.includes('\n') || (messageText.match(/,/g) || []).length > 2
};

return [{
  json: {
    originalMessage: {
      phoneNumber: messageData.phoneNumber,
      messageType: messageData.messageType,
      imageMessage: messageData.imageMessage,
      imageCaption: messageData.imageCaption,
      pushName: messageData.pushName,
      messageText: messageData
    },
    classification: {
      type: classification,
      confidence: getConfidence(messageText, classification),
      reasoning: getReasoningLog(messageText, classification)
    },
    routing: config[classification],
    analysis: {
      messageLength: messageText.length,
      wordCount: messageText.split(/\s+/).length,
      specialFlags: specialFlags,
      processingTime: new Date().toISOString()
    }
  }
}];

// Função para calcular confiança na classificação
function getConfidence(text, classification) {
  const wordCount = text.split(/\s+/).length;

  if (classification === 'EXPRESS' && wordCount <= 2) return 0.95;
  if (classification === 'VISION') return 1.0;
  if (classification === 'NUTRITION' && /\d+.*?(g|ml|kg)/i.test(text)) return 0.9;

  return 0.75; // Confiança padrão
}

// Log do raciocínio para debug
function getReasoningLog(text, classification) {
  return {
    textLength: text.length,
    wordCount: text.split(/\s+/).length,
    hasNumbers: /\d/.test(text),
    hasUnits: /\d+.*?(g|ml|kg|unidade)/i.test(text),
    classification: classification
  };
}
