function normalizePhoneNumber(phoneNumber) {
  const cleanNumber = phoneNumber.split('@')[0];
  // Remove 9 extra de n√∫meros brasileiros: 5585999028259 -> 558599028259
  return cleanNumber.replace(/^(55\d{2})9(\d{8})$/, '$1$2');
