// Fun√ß√£o para detectar produtos Herbalife
function detectHerbalifeProducts(messageText) {
  const herbalifeKeywords = [
    'herbalife', 'shake herbalife', 'nutrev', 'fiber concentrate', 
    'fiber powder', 'barra de prote√≠na', 'protein powder', 'protein crunch',
    'nutri soup', 'sopa instant√¢nea', 'cr7 drive', 'liftoff', 
    'tri-core protein', 'whey protein 3w', 'shake', 'nutreV'
  ];

  const normalizedText = messageText.toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/[^\w\s]/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();

  const hasHerbalifeKeyword = herbalifeKeywords.some(keyword => 
    normalizedText.includes(keyword.toLowerCase())
  );

  if (!hasHerbalifeKeyword) {
    return { hasHerbalife: false, searchTerms: [] };
  }

  const productPatterns = [
    /shake\s*(herbalife)?\s*(baunilha|chocolate|morango|frutas vermelhas)?/gi,
    /nutrev?/gi,
    /fiber\s*(concentrate|powder)/gi,
    /barra\s*de\s*prote√≠na/gi,
    /protein\s*(powder|crunch)/gi,
    /nutri\s*soup/gi,
    /sopa\s*instant√¢nea/gi,
    /cr7\s*drive/gi,
    /liftoff/gi,
    /tri-?core\s*protein/gi,
    /whey\s*protein\s*3w/gi
  ];

  const detectedTerms = [];
  productPatterns.forEach(pattern => {
    const matches = messageText.match(pattern);
    if (matches) {
      detectedTerms.push(...matches);
    }
  });

  const quantityPatterns = [
    /(\d+(?:\.\d+)?)\s*(?:g|gramas?|ml|mililitros?)/gi,
    /(\d+(?:\.\d+)?)\s*(?:colheres?|medidas?|scoops?)/gi,
    /(\d+(?:\.\d+)?)\s*(?:por√ß√µes?|doses?)/gi
  ];

  const quantities = [];
  quantityPatterns.forEach(pattern => {
    const matches = [...messageText.matchAll(pattern)];
    matches.forEach(match => {
      quantities.push({
        value: parseFloat(match[1]),
        unit: match[0].replace(match[1], '').trim(),
        fullMatch: match[0]
      });
    });
  });

  return {
    hasHerbalife: true,
    searchTerms: [...new Set(detectedTerms.map(term => term.trim()))],
    quantities: quantities,
    confidence: detectedTerms.length > 0 ? 'high' : 'medium'
  };
}

// Determinar tipo de an√°lise nutricional necess√°ria
function analyzeNutritionRequest(text, messageData) {
  const lowerText = text.toLowerCase().trim();

  // Tipos de an√°lise
  const analysisType = {
    FOOD_REGISTRATION: 'registro_alimento',
    WATER_INTAKE: 'consumo_agua', 
    MEAL_ANALYSIS: 'analise_refeicao',
    DAILY_SUMMARY: 'resumo_diario',
    NUTRITION_QUERY: 'consulta_nutricional',
    GOAL_TRACKING: 'acompanhamento_meta'
  };

  // Detectar tipo de solicita√ß√£o
  if (/^bebi \d+ml|\d+ml de √°gua|(√°gua|hidrat)/i.test(lowerText)) {
    return {
      type: analysisType.WATER_INTAKE,
      complexity: 'low',
      estimatedTokens: 800
    };
  }

  if (/(comi|almocei|jantei|lanchei|tomei caf√©)/i.test(lowerText)) {
    return {
      type: analysisType.FOOD_REGISTRATION,
      complexity: 'medium',
      estimatedTokens: 1500
    };
  }

  if (/^(resumo|como estamos|minhas metas|total de hoje)/i.test(lowerText)) {
    return {
      type: analysisType.DAILY_SUMMARY,
      complexity: 'high',
      estimatedTokens: 2500
    };
  }

  if (/(relat√≥rio|an√°lise completa|feedback|balan√ßo)/i.test(lowerText)) {
    return {
      type: analysisType.MEAL_ANALYSIS,
      complexity: 'high',
      estimatedTokens: 3000
    };
  }

  if (/(d√©ficit|super√°vit|meta di√°ria|objetivo)/i.test(lowerText)) {
    return {
      type: analysisType.GOAL_TRACKING,
      complexity: 'medium',
      estimatedTokens: 1800
    };
  }

  // Default para consultas nutricionais
  return {
    type: analysisType.NUTRITION_QUERY,
    complexity: 'medium',
    estimatedTokens: 1200
  };
}

// Extrair informa√ß√µes nutricionais do texto
function extractNutritionData(text) {
  const data = {
    foods: [],
    quantities: [],
    units: [],
    liquids: [],
    timeOfDay: null,
    mealType: null
  };

  // Extrair alimentos comuns
  const foodMatches = text.match(/(arroz|feij√£o|carne|frango|peixe|ovo|p√£o|leite|queijo|presunto|salada|tomate|alface|batata|macarr√£o|massa|suco|caf√©|ch√°|a√ß√∫car|sal|√≥leo|manteiga)/gi);
  if (foodMatches) {
    data.foods = [...new Set(foodMatches.map(f => f.toLowerCase()))];
  }

  // Extrair quantidades
  const quantityMatches = text.match(/\d+\s*(g|ml|mg|kg|unidade|fatia|colher|x√≠cara|copo|litro)/gi);
  if (quantityMatches) {
    data.quantities = quantityMatches;
  }

  // Detectar tipo de refei√ß√£o
  if (/(caf√© da manh√£|manh√£)/i.test(text)) data.mealType = 'cafe_manha';
  else if (/(almo√ßo|meio dia)/i.test(text)) data.mealType = 'almoco';
  else if (/(lanche|tarde)/i.test(text)) data.mealType = 'lanche';
  else if (/(jantar|janta|noite)/i.test(text)) data.mealType = 'jantar';

  // Detectar hor√°rio
  const timeMatch = text.match(/(\d{1,2}):?(\d{2})?\s*(h|hora|horas)?/i);
  if (timeMatch) {
    data.timeOfDay = timeMatch[0];
  }

  return data;
}

